import React, { Component } from 'react'
import jwt_decode from 'jwt-decode'
//import formForUser from './formForUser'
class Profile extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ID : props.match.params.id,
      first_name: '',
      last_name: '',
      email: '',
      errors: {},
      hits: [],
      isUser: false,
    }
  }
  isThisUser() {
    const token = localStorage.getItem("usertoken")
    const decoded = jwt_decode(token, {header:true})
    if (this.state.ID == decoded.id) {
      this.setState({isUser: true})
    } else {
      this.setState({isUser: false})
    }
  }
  componentDidMount() {
    fetch(`http://localhost:5000/users/profile/${this.state.ID}`)
    .then(response => response.json())
    .then(data => {
      this.setState({hits:data});
      console.log(this.state.hits);
    })
    this.isThisUser();
  }

  render() {
    return (
      <div className="container">
        <div className="jumbotron mt-5">
          <div className="col-sm-8 mx-auto">
            <h1 className="text-center">PROFILE</h1>

          </div>
          <table className="table col-md-6 mx-auto">
            <tbody>
              <tr>
                <td>Fist Name</td>
                <td>{this.state.hits.first_name}</td>
              </tr>
              <tr>
                <td>Last Name</td>
                <td>{this.state.hits.last_name}</td>
              </tr>
              <tr>
                <td>Email</td>
                <td>{this.state.hits.email}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    )
  }
}

export default Profile
