import React, { Component } from 'react'
import { like } from './UserFunctions'
 import "../styles/questions.css";
//const API = "http://localhost:5000/questions/"
class Post extends Component {

  constructor(props) {
    super(props);
      this.state = {
        ID : props.match.params.id,
        hits:[],
        user:[],
    };

    this.changeComment = this.changeComment.bind(this)
    this.commentFunction = this.commentFunction.bind(this)
  }



  changeComment(e) {
    this.setState({ [e.target.name]: e.target.value })
  }
  onSubmit(e) {
    e.preventDefault()
    const likeData = {
      post_id: this.state.ID,
      user_id: this.state.user_id,
    }

    like(likeData).then(res => {
      if (res) {
        this.props.history.push(`/post/${this.state.ID}`)
      }
    })
  }
componentDidMount(){
  let id = this.state.ID;
  console.log(id);
  fetch(`http://localhost:5000/questions/${id}`)
  .then(response => response.json())
  .then(data => {
    this.setState({hits:data});
    this.setState({user:data.user})
    console.log(this.state.user);
  })
}
  render() {

    const commentsForUser = (
      <div className="comment col-md-8 mx-auto">
        <form className="commentForm" onSubmit={this.commentFunction}>
          <div className='row'>
            <div className='col-md-6 mx-auto'>
              <input type='text' className='form-control' placeholder="Comment..."  value={this.state.commentText} onChange={this.changeComment} name='commentText'/>
            </div>
            <div className="col-md-3 mx-auto">
              <button type='submit' className='btn btn-outline-light form-control'>Comment</button>
            </div>
          </div>
        </form>
      </div>
    )

    const div = (
      <div></div>
    )
    return (


        <div className="container-fluid">
        <h3>Latest Questions</h3>
        <div className='row'>
          {
                <div className="post col-md-8 mt-5 mx-auto">
                    <div className="smallLine col-md-12">
                      <p>{this.state.user.first_name} {this.state.user.last_name}</p>
                      <p style={{textAlign:"right"}}>{this.state.hits.createdAt}</p>
                    </div>
                    <h5>{this.state.hits.questionTitle}</h5>
                    <p className="textPost">{this.state.hits.questionText}</p>
                    <br/>
                    <div className="smallLineIcons col-md-12">
                      <div className="forms">
                        <button type='submit' className="btn btnDarkWhite" onClick={this.onSubmit}><i className="fa fa-thumbs-up icons"></i></button>
                        <form><button type='submit' className="btn btnDarkWhite"><i className="fa fa-thumbs-down icons"></i></button></form>
                      </div>
                    </div>
                </div>
          }
                              {localStorage.usertoken ? commentsForUser : div}

        </div>
      </div>
    );
  }
}

export default Post;
