import React, { Component } from 'react'
import { newQuestion } from './UserFunctions'
import "../styles/login.css";
class NewQuestion extends Component {
  constructor() {
    super()
    this.state = {
      questionTitle: '',
      questionText: '',
      errors: {}
    }

    this.onChange = this.onChange.bind(this)
    this.onSubmit = this.onSubmit.bind(this)
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }
  onSubmit(e) {
    e.preventDefault()

    const new_question = {
      questionTitle: this.state.questionTitle,
      questionText: this.state.questionText
    }

    newQuestion(new_question).then(res => {
      if (res) {
        this.props.history.push('/profile')
      }
    })
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-6 mt-5 mx-auto">
            <form noValidate onSubmit={this.onSubmit}>
              <h1 className="h3 mb-3 font-weight-normal">Please fill the form</h1>
              <div className="form-group">
                <label htmlFor="email">Question title</label>
                <input
                  type="text"
                  className="form-control"
                  name="questionTitle"
                  placeholder="Enter title"
                  value={this.state.questionTitle}
                  onChange={this.onChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="password">Question text</label>
                <input
                  type="password"
                  className="form-control"
                  name="password"
                  placeholder="Password"
                  value={this.state.password}
                  onChange={this.onChange}
                />
              </div>
              <button
                type="submit"
                className="btn btn-lg btn-primary btn-block"
              >
                Sign in
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default NewQuestion
