import React, { Component } from 'react'
import { Link, withRouter } from 'react-router-dom'

import {posts} from "./UserFunctions";
 import "../styles/questions.css";
const API = "http://localhost:5000/questions/showAll"
class Landing extends Component {

  constructor(props) {
    super(props);
      this.state = {
        questionTitle: '',
        questionText: '',
        user_id: localStorage.getItem("usertoken"),
        errors: {},
        hits:[],
    };

    this.onChange = this.onChange.bind(this)
    this.onSubmit = this.onSubmit.bind(this)
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }
  onSubmit(e) {
    e.preventDefault()
    const new_question = {
      questionTitle: this.state.questionTitle,
      questionText: this.state.questionText,
      user_id: this.state.user_id,
    }

    posts(new_question).then(res => {
      if (res) {
        this.props.history.push('/')
      }
    })
  }

componentDidMount(){
  fetch(API)
  .then(response => response.json())
  .then(data => this.setState({hits:data}))
}
  render() {
    return (

        <div className="container">
          <br/>
            <div className="row">
          <div className="col-md-8 mt-5 mx-auto">
            <form noValidate onSubmit={this.onSubmit}>
            <h3>Post a new question</h3>
              <div className="form-group">
                <label htmlFor="questionTitle">Question Title</label>
                <input
                  type="text"
                  className="form-control"
                  name="questionTitle"
                  placeholder="Enter title"
                  value={this.state.questionTitle}
                  onChange={this.onChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="questionText">Question Text</label>
                <textarea
                  className="form-control"
                  name="questionText"
                  placeholder="Text"
                  value={this.state.questionText}
                  onChange={this.onChange}
                />
              </div>
              <button
                type="submit"
                className="btn btn-md btn-primary btn-block"
              >
                Post the question
              </button>
            </form>
          </div>
        </div><br/>
        <h3>Latest Questions</h3>
        <div className='row'>
          {
            this.state.hits.map(hit =>
                <div className="post col-md-8 mt-5 mx-auto">
                    <h5>{hit.questionTitle}</h5>
                      <Link to="/profile" className="linked">
                        Read more
                      </Link>
                    <br/>
                </div>
              )
          }
        </div>
      </div>
    );
  }
}

export default Landing;
