import React, { Component } from 'react'
import { Link, withRouter } from 'react-router-dom'
import {posts, sendData} from "./UserFunctions";
 import "../styles/questions.css";
const API = "http://localhost:5000/questions/showAll"
class Post extends Component {

  constructor(props) {
    super(props);
      this.state = {
        questionTitle: '',
        questionText: '',
        user_id: localStorage.getItem("usertoken"),
        errors: {},
        hits:[],
    };

    this.onChange = this.onChange.bind(this)
    this.onSubmit = this.onSubmit.bind(this)
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }
  onSubmit(e) {
    e.preventDefault()
    const new_question = {
      questionTitle: this.state.questionTitle,
      questionText: this.state.questionText,
      user_id: this.state.user_id,
    }

    posts(new_question).then(res => {
      if (res) {
        this.props.history.push('/')
      }
    })
  }

componentDidMount(){
  fetch(API)
  .then(response => response.json())
  .then(data => this.setState({hits:data}))
}
  render() {
    return (

        <div className="container">
        <h3>Latest Questions</h3>
        <div className='row'>
          {
            this.state.hits.map(hit =>
                <div className="post col-md-8 mt-5 mx-auto">
                    <h5>{hit.questionTitle}</h5>
                      <Link to={`/post/${hit.id}`} className="linked" onClick={sendData(hit.id)}>
                        Read more...
                      </Link>
                    <br/>
                </div>
              )
          }
        </div>
      </div>
    );
  }
}

export default Post;
