import React, { Component } from 'react'
import jwt_decode from 'jwt-decode'

class Profile extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ID : props.match.params.id,
      first_name: '',
      last_name: '',
      email: '',
      user_id: 0,
      errors: {},
      hits: [],
    }
  }

  componentDidMount() {
    const token = localStorage.getItem("usertoken")
    const decoded = jwt_decode(token)
    this.setState({user_id:decoded.id})
    fetch(`http://localhost:5000/users/profile/${this.state.ID}`)
    .then(response => response.json())
    .then(data => {
      this.setState({hits:data});
      console.log(this.state.hits);
    })
  }

  render() {
    const div = (
      <div></div>
    )
    const userForm = (
      <button>Change</button>
    )
    return (
      <div className="container">
        <div className="jumbotron mt-5">
          <div className="col-sm-8 mx-auto">
            <h1 className="text-center">PROFILE</h1>
          </div>
          <table className="table col-md-6 mx-auto">
          {this.state.ID == this.state.user_id ? userForm : div}

            <tbody>
              <tr>
                <td>Fist Name</td>
                <td>{this.state.hits.first_name}</td>
              </tr>
              <tr>
                <td>Last Name</td>
                <td>{this.state.hits.last_name}</td>
              </tr>
              <tr>
                <td>Email</td>
                <td>{this.state.hits.email}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    )
  }
}

export default Profile
